mapper.select:
SELECT TS.SITTERNUMBER sitterNumber, TB.USERNUMBER userNumber, TB.BOOKINGNUMBER bookingNumber, TBS.TITLE title, TBS.START start, TBS.END end, TBS.DESCRIPTION description, TB.PROGRESSSTATE progressState
FROM TBL_BOOKING AS TB, TBL_BOOKING_SCHEDULE AS TBS, TBL_SITTER AS TS
WHERE TBS.BOOKINGNUMBER = TB.BOOKINGNUMBER AND TB.SITTERNUMBER = TS.SITTERNUMBER AND TS.SITTERNUMBER= #{sitterNumber};

SELECT TS.SITTERNUMBER, U.USERNAME, UP.PETNAME, TB.USERNUMBER, TB.BOOKINGDATE, TB.BOOKINGNUMBER , UP.PETTYPE, UP.PETGENDER, UP.PETDATEOFBIRTH 
FROM TBL_BOOKING AS TB, TBL_USER AS U, TBL_USER_PET AS UP, TBL_SITTER AS TS
WHERE U.USERNUMBER = TB.USERNUMBER AND U.USERNUMBER = UP.USERNUMBER AND TB.SITTERNUMBER = TS.SITTERNUMBER AND TB.BOOKINGNUMBER = #{bookingNumber};
